<script>
export default {
    mounted() {
        // window.console.log('DI: ');
        // window.console.log(this._device);
    },

    props: {
        device: [Object]
    },

    computed: {
        wideStrokeColor() {
            let color = this.$vuetify.theme.dark ? 'lightgray' : 'gray';
            return color;
        },

        topHemicircleColor() {
            return this.device.config.hotWaterCounterEnabled
                ? this.$store.state.const.AquaBastRedColor
                : this.$store.state.const.AquaBastBlueColor;
        },

        bottomHemicircleColor() {
            return this.device.config.coldWaterCounterEnabled
                ? this.$store.state.const.AquaBastBlueColor
                : this.$store.state.const.AquaBastRedColor;
        }
    },

    data() {
        return {};
    }
};
</script>
